import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { 
  FiPhone, 
  FiMessageCircle, 
  FiCheck, 
  FiCheckCircle, 
  FiClock, 
  FiUser, 
  FiSmartphone,
  FiMapPin,
  FiRefreshCw,
  FiEye,
  FiX
} from 'react-icons/fi';
import { FaWhatsapp } from 'react-icons/fa';
import toast from 'react-hot-toast';
import axios from 'axios';

const AdminDashboard = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalBookings: 0,
    pendingBookings: 0,
    completedBookings: 0,
    todayBookings: 0
  });
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Check if user is admin
  useEffect(() => {
    if (!isAuthenticated || (user && user.role !== 'admin')) {
      toast.error('Access denied. Admin login required.');
      navigate('/login');
      return;
    }
    fetchBookings();
  }, [isAuthenticated, user, navigate]);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      
      // Fetch bookings from API
      const response = await axios.get('/api/admin/bookings', {
        headers: {
          'x-auth-token': localStorage.getItem('token')
        }
      });

      if (response.data.success) {
        setBookings(response.data.bookings);
        
        // Calculate stats
        const totalBookings = response.data.bookings.length;
        const pendingBookings = response.data.bookings.filter(b => b.status === 'pending').length;
        const completedBookings = response.data.bookings.filter(b => b.status === 'completed').length;
        const todayBookings = response.data.bookings.filter(b => {
          const today = new Date().toDateString();
          const bookingDate = new Date(b.createdAt).toDateString();
          return today === bookingDate;
        }).length;

        setStats({
          totalBookings,
          pendingBookings,
          completedBookings,
          todayBookings
        });
      }

    } catch (error) {
      console.error('Error fetching bookings:', error);
      
      // Fallback to demo data if API fails
      const demoBookings = [
        {
          _id: '1',
          customerDetails: {
            name: 'Rahul Kumar',
            phone: '9876543210',
            email: 'rahul@example.com',
            address: 'Sector 15, Noida, UP'
          },
          deviceBrand: 'Apple (iPhone)',
          deviceModel: 'iPhone 14 Pro',
          serviceType: 'Display/Screen Issues',
          issueDescription: 'Screen is cracked and touch is not working properly',
          status: 'pending',
          preferredDate: '2024-01-15',
          preferredTime: '2:00 PM - 4:00 PM',
          createdAt: new Date().toISOString(),
          isGuestBooking: false
        },
        {
          _id: '2',
          customerDetails: {
            name: 'Priya Sharma',
            phone: '9123456789',
            email: 'priya@example.com',
            address: 'Dwarka, New Delhi'
          },
          deviceBrand: 'Samsung',
          deviceModel: 'Galaxy S23',
          serviceType: 'Battery Problems',
          issueDescription: 'Battery drains very fast, need replacement',
          status: 'accepted',
          preferredDate: '2024-01-16',
          preferredTime: '10:00 AM - 12:00 PM',
          createdAt: new Date(Date.now() - 86400000).toISOString(),
          isGuestBooking: true
        },
        {
          _id: '3',
          customerDetails: {
            name: 'Amit Singh',
            phone: '9800999600',
            email: 'amit@example.com',
            address: 'Gurgaon, Haryana'
          },
          deviceBrand: 'OnePlus',
          deviceModel: 'OnePlus 11',
          serviceType: 'Charging Port Issues',
          issueDescription: 'Phone not charging, port seems loose',
          status: 'completed',
          preferredDate: '2024-01-14',
          preferredTime: '4:00 PM - 6:00 PM',
          createdAt: new Date(Date.now() - 172800000).toISOString(),
          isGuestBooking: false
        }
      ];

      setBookings(demoBookings);
      
      // Calculate stats for demo data
      const totalBookings = demoBookings.length;
      const pendingBookings = demoBookings.filter(b => b.status === 'pending').length;
      const completedBookings = demoBookings.filter(b => b.status === 'completed').length;
      const todayBookings = demoBookings.filter(b => {
        const today = new Date().toDateString();
        const bookingDate = new Date(b.createdAt).toDateString();
        return today === bookingDate;
      }).length;

      setStats({
        totalBookings,
        pendingBookings,
        completedBookings,
        todayBookings
      });
      
      toast.error('Using demo data - API not available');
    } finally {
      setLoading(false);
    }
  };

  const handleCall = (phone) => {
    window.open(`tel:${phone}`, '_self');
  };

  const handleWhatsApp = (phone, customerName, deviceBrand, issue) => {
    const message = `Hello ${customerName}, this is Mobi repair. We received your repair request for ${deviceBrand} - ${issue}. We will contact you soon for pickup.`;
    const whatsappUrl = `https://wa.me/91${phone.replace(/\D/g, '')}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const updateBookingStatus = async (bookingId, newStatus) => {
    try {
      // Demo implementation - replace with actual API call
      setBookings(prev => prev.map(booking => 
        booking._id === bookingId 
          ? { ...booking, status: newStatus }
          : booking
      ));
      
      toast.success(`Booking ${newStatus} successfully!`);
      
      // Update stats
      fetchBookings();
    } catch (error) {
      console.error('Error updating booking:', error);
      toast.error('Failed to update booking');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30';
      case 'accepted': return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
      case 'completed': return 'bg-green-500/20 text-green-400 border-green-500/30';
      default: return 'bg-gray-500/20 text-gray-400 border-gray-500/30';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending': return <FiClock className="w-4 h-4" />;
      case 'accepted': return <FiCheck className="w-4 h-4" />;
      case 'completed': return <FiCheckCircle className="w-4 h-4" />;
      default: return <FiClock className="w-4 h-4" />;
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pink-500 mx-auto mb-4"></div>
          <p className="text-gray-300">Loading admin dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 text-white">
      {/* Header */}
      <div className="bg-gradient-to-r from-pink-500/10 to-purple-500/10 border-b border-pink-500/20 py-8">
        <div className="container-max">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-white mb-2">Admin Dashboard</h1>
              <p className="text-gray-300">Manage repair bookings and customer requests</p>
            </div>
            <button
              onClick={fetchBookings}
              className="flex items-center space-x-2 bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700 text-white px-4 py-2 rounded-lg transition-all duration-300"
            >
              <FiRefreshCw className="w-4 h-4" />
              <span>Refresh</span>
            </button>
          </div>
        </div>
      </div>

      <div className="container-max py-8">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-gradient-to-br from-blue-500/10 to-blue-600/10 border border-blue-500/20 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-blue-400 text-sm font-medium">Total Bookings</p>
                <p className="text-2xl font-bold text-white">{stats.totalBookings}</p>
              </div>
              <div className="text-blue-400 text-2xl"></div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-yellow-500/10 to-yellow-600/10 border border-yellow-500/20 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-yellow-400 text-sm font-medium">Pending</p>
                <p className="text-2xl font-bold text-white">{stats.pendingBookings}</p>
              </div>
              <div className="text-yellow-400 text-2xl"></div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-green-500/10 to-green-600/10 border border-green-500/20 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-400 text-sm font-medium">Completed</p>
                <p className="text-2xl font-bold text-white">{stats.completedBookings}</p>
              </div>
              <div className="text-green-400 text-2xl"></div>
            </div>
          </div>

          <div className="bg-gradient-to-br from-purple-500/10 to-purple-600/10 border border-purple-500/20 rounded-xl p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-purple-400 text-sm font-medium">Today</p>
                <p className="text-2xl font-bold text-white">{stats.todayBookings}</p>
              </div>
              <div className="text-purple-400 text-2xl"></div>
            </div>
          </div>
        </div>

        {/* Bookings Table */}
        <div className="bg-gradient-to-br from-gray-800/90 to-gray-900/90 backdrop-blur-lg rounded-2xl border border-gray-700/50 overflow-hidden">
          <div className="p-6 border-b border-gray-700/50">
            <h2 className="text-xl font-bold text-white">Repair Bookings</h2>
            <p className="text-gray-400 text-sm mt-1">Manage all customer repair requests</p>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-800/50">
                <tr>
                  <th className="text-left p-4 text-gray-300 font-medium">Customer</th>
                  <th className="text-left p-4 text-gray-300 font-medium">Device</th>
                  <th className="text-left p-4 text-gray-300 font-medium">Issue</th>
                  <th className="text-left p-4 text-gray-300 font-medium">Status</th>
                  <th className="text-left p-4 text-gray-300 font-medium">Date</th>
                  <th className="text-left p-4 text-gray-300 font-medium">Actions</th>
                </tr>
              </thead>
              <tbody>
                {bookings.map((booking) => (
                  <tr key={booking._id} className="border-t border-gray-700/30 hover:bg-gray-800/30 transition-colors">
                    <td className="p-4">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-gradient-to-r from-pink-500 to-purple-600 rounded-full flex items-center justify-center">
                          <FiUser className="w-5 h-5 text-white" />
                        </div>
                        <div>
                          <p className="text-white font-medium">{booking.customerDetails.name}</p>
                          <p className="text-gray-400 text-sm">{booking.customerDetails.phone}</p>
                        </div>
                      </div>
                    </td>
                    <td className="p-4">
                      <div className="flex items-center space-x-2">
                        <FiSmartphone className="w-4 h-4 text-blue-400" />
                        <div>
                          <p className="text-white font-medium">{booking.deviceBrand}</p>
                          <p className="text-gray-400 text-sm">{booking.deviceModel || 'Model not specified'}</p>
                        </div>
                      </div>
                    </td>
                    <td className="p-4">
                      <div>
                        <p className="text-white font-medium">{booking.serviceType}</p>
                        <p className="text-gray-400 text-sm truncate max-w-xs">{booking.issueDescription}</p>
                      </div>
                    </td>
                    <td className="p-4">
                      <span className={`inline-flex items-center space-x-1 px-3 py-1 rounded-full text-xs font-medium border ${getStatusColor(booking.status)}`}>
                        {getStatusIcon(booking.status)}
                        <span className="capitalize">{booking.status}</span>
                      </span>
                    </td>
                    <td className="p-4">
                      <p className="text-gray-300 text-sm">{formatDate(booking.createdAt)}</p>
                    </td>
                    <td className="p-4">
                      <div className="flex items-center space-x-2">
                        <button
                          onClick={() => handleCall(booking.customerDetails.phone)}
                          className="p-2 bg-green-500/20 hover:bg-green-500/30 text-green-400 rounded-lg transition-colors"
                          title="Call Customer"
                        >
                          <FiPhone className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleWhatsApp(
                            booking.customerDetails.phone,
                            booking.customerDetails.name,
                            booking.deviceBrand,
                            booking.serviceType
                          )}
                          className="p-2 bg-green-600/20 hover:bg-green-600/30 text-green-400 rounded-lg transition-colors"
                          title="WhatsApp Customer"
                        >
                          <FaWhatsapp className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => {
                            setSelectedBooking(booking);
                            setShowModal(true);
                          }}
                          className="p-2 bg-blue-500/20 hover:bg-blue-500/30 text-blue-400 rounded-lg transition-colors"
                          title="View Details"
                        >
                          <FiEye className="w-4 h-4" />
                        </button>
                        {booking.status === 'pending' && (
                          <button
                            onClick={() => updateBookingStatus(booking._id, 'accepted')}
                            className="p-2 bg-blue-500/20 hover:bg-blue-500/30 text-blue-400 rounded-lg transition-colors"
                            title="Accept Booking"
                          >
                            <FiCheck className="w-4 h-4" />
                          </button>
                        )}
                        {booking.status === 'accepted' && (
                          <button
                            onClick={() => updateBookingStatus(booking._id, 'completed')}
                            className="p-2 bg-green-500/20 hover:bg-green-500/30 text-green-400 rounded-lg transition-colors"
                            title="Mark Completed"
                          >
                            <FiCheckCircle className="w-4 h-4" />
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {bookings.length === 0 && (
            <div className="text-center py-12">
              <div className="text-6xl mb-4"></div>
              <h3 className="text-xl font-semibold text-white mb-2">No Bookings Yet</h3>
              <p className="text-gray-400">New repair bookings will appear here</p>
            </div>
          )}
        </div>
      </div>

      {/* Booking Details Modal */}
      {showModal && selectedBooking && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl border border-gray-700 max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6 border-b border-gray-700">
              <div className="flex items-center justify-between">
                <h3 className="text-xl font-bold text-white">Booking Details</h3>
                <button
                  onClick={() => setShowModal(false)}
                  className="p-2 hover:bg-gray-700 rounded-lg transition-colors"
                >
                  <FiX className="w-5 h-5 text-gray-400" />
                </button>
              </div>
            </div>
            
            <div className="p-6 space-y-6">
              {/* Customer Info */}
              <div>
                <h4 className="text-lg font-semibold text-white mb-3">Customer Information</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <p className="text-gray-400 text-sm">Name</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.name}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Phone</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.phone}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Email</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.email}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Address</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.address}</p>
                  </div>
                </div>
              </div>

              {/* Device Info */}
              <div>
                <h4 className="text-lg font-semibold text-white mb-3">Device Information</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <p className="text-gray-400 text-sm">Brand</p>
                    <p className="text-white font-medium">{selectedBooking.deviceBrand}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Model</p>
                    <p className="text-white font-medium">{selectedBooking.deviceModel || 'Not specified'}</p>
                  </div>
                </div>
              </div>

              {/* Issue Details */}
              <div>
                <h4 className="text-lg font-semibold text-white mb-3">Issue Details</h4>
                <div>
                  <p className="text-gray-400 text-sm">Issue Type</p>
                  <p className="text-white font-medium mb-3">{selectedBooking.serviceType}</p>
                  <p className="text-gray-400 text-sm">Description</p>
                  <p className="text-white">{selectedBooking.issueDescription}</p>
                </div>
              </div>

              {/* Schedule */}
              {selectedBooking.preferredDate && (
                <div>
                  <h4 className="text-lg font-semibold text-white mb-3">Preferred Schedule</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <p className="text-gray-400 text-sm">Date</p>
                      <p className="text-white font-medium">{selectedBooking.preferredDate}</p>
                    </div>
                    <div>
                      <p className="text-gray-400 text-sm">Time</p>
                      <p className="text-white font-medium">{selectedBooking.preferredTime || 'Any time'}</p>
                    </div>
                  </div>
                </div>
              )}

              {/* Action Buttons */}
              <div className="flex flex-wrap gap-3 pt-4 border-t border-gray-700">
                <button
                  onClick={() => handleCall(selectedBooking.customerDetails.phone)}
                  className="flex items-center space-x-2 bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  <FiPhone className="w-4 h-4" />
                  <span>Call Customer</span>
                </button>
                <button
                  onClick={() => handleWhatsApp(
                    selectedBooking.customerDetails.phone,
                    selectedBooking.customerDetails.name,
                    selectedBooking.deviceBrand,
                    selectedBooking.serviceType
                  )}
                  className="flex items-center space-x-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  <FaWhatsapp className="w-4 h-4" />
                  <span>WhatsApp</span>
                </button>
                {selectedBooking.status === 'pending' && (
                  <button
                    onClick={() => {
                      updateBookingStatus(selectedBooking._id, 'accepted');
                      setShowModal(false);
                    }}
                    className="flex items-center space-x-2 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg transition-colors"
                  >
                    <FiCheck className="w-4 h-4" />
                    <span>Accept</span>
                  </button>
                )}
                {selectedBooking.status === 'accepted' && (
                  <button
                    onClick={() => {
                      updateBookingStatus(selectedBooking._id, 'completed');
                      setShowModal(false);
                    }}
                    className="flex items-center space-x-2 bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg transition-colors"
                  >
                    <FiCheckCircle className="w-4 h-4" />
                    <span>Mark Completed</span>
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
