import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { FiMinus, FiPlus, FiTrash2, FiShoppingBag, FiArrowLeft } from 'react-icons/fi';
import axios from 'axios';
import toast from 'react-hot-toast';

const Cart = () => {
  const { items, total, updateQuantity, removeFromCart, clearCart } = useCart();
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [shippingAddress, setShippingAddress] = useState({
    name: '',
    phone: '',
    email: '',
    street: '',
    city: '',
    state: '',
    pincode: ''
  });

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  const calculateShipping = () => {
    return total >= 999 ? 0 : 49;
  };

  const calculateTax = () => {
    return Math.round(total * 0.18); // 18% GST
  };

  const calculateFinalTotal = () => {
    return total + calculateShipping() + calculateTax();
  };

  const handleQuantityChange = (productId, newQuantity) => {
    if (newQuantity < 1) {
      removeFromCart(productId);
    } else {
      updateQuantity(productId, newQuantity);
    }
  };

  const handleAddressChange = (e) => {
    setShippingAddress({
      ...shippingAddress,
      [e.target.name]: e.target.value
    });
  };

  const validateAddress = () => {
    const required = ['name', 'phone', 'street', 'city', 'state', 'pincode'];
    return required.every(field => shippingAddress[field].trim() !== '');
  };

  const handleCheckout = async () => {
    if (!isAuthenticated) {
      toast.error('Please login to continue');
      navigate('/login', { state: { from: { pathname: '/cart' } } });
      return;
    }

    if (!validateAddress()) {
      toast.error('Please fill in all required address fields');
      return;
    }

    setLoading(true);

    try {
      const orderData = {
        items: items.map(item => ({
          product: item.product._id,
          quantity: item.quantity
        })),
        shippingAddress,
        paymentMethod: 'COD', // Default to Cash on Delivery
        notes: ''
      };

      const response = await axios.post('/api/orders', orderData);
      
      if (response.data.success) {
        toast.success('Order placed successfully!');
        clearCart();
        navigate('/dashboard');
      }
    } catch (error) {
      console.error('Checkout error:', error);
      toast.error(error.response?.data?.message || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  if (items.length === 0) {
    return (
      <div className="min-h-screen section-padding">
        <div className="container-max">
          <div className="text-center py-16">
            <div className="text-8xl mb-6">ðŸ›’</div>
            <h2 className="text-2xl font-bold text-white mb-4">Your cart is empty</h2>
            <p className="text-dark-300 mb-8">
              Looks like you haven't added any items to your cart yet.
            </p>
            <Link to="/store" className="btn-primary inline-flex items-center space-x-2">
              <FiShoppingBag className="w-5 h-5" />
              <span>Continue Shopping</span>
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen section-padding">
      <div className="container-max">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-white mb-2">Shopping Cart</h1>
            <p className="text-dark-300">{items.length} items in your cart</p>
          </div>
          <Link
            to="/store"
            className="btn-outline flex items-center space-x-2"
          >
            <FiArrowLeft className="w-4 h-4" />
            <span>Continue Shopping</span>
          </Link>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Cart Items */}
          <div className="lg:col-span-2 space-y-4">
            {items.map((item) => (
              <div key={item.product._id} className="card">
                <div className="flex items-center space-x-4">
                  {/* Product Image */}
                  <img
                    src={item.product.images?.[0] || 'https://via.placeholder.com/100x100?text=Product'}
                    alt={item.product.name}
                    className="w-20 h-20 object-cover rounded-lg"
                  />

                  {/* Product Details */}
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-white mb-1">
                      {item.product.name}
                    </h3>
                    <p className="text-dark-300 text-sm mb-2">
                      {item.product.description}
                    </p>
                    <div className="flex items-center space-x-4">
                      <span className="text-primary-400 font-semibold">
                        {formatCurrency(item.product.price)}
                      </span>
                      {item.product.originalPrice && item.product.originalPrice > item.product.price && (
                        <span className="text-dark-400 line-through text-sm">
                          {formatCurrency(item.product.originalPrice)}
                        </span>
                      )}
                    </div>
                  </div>

                  {/* Quantity Controls */}
                  <div className="flex items-center space-x-3">
                    <button
                      onClick={() => handleQuantityChange(item.product._id, item.quantity - 1)}
                      className="w-8 h-8 bg-dark-700 hover:bg-dark-600 rounded-full flex items-center justify-center text-white transition-colors"
                    >
                      <FiMinus className="w-4 h-4" />
                    </button>
                    <span className="text-white font-medium w-8 text-center">
                      {item.quantity}
                    </span>
                    <button
                      onClick={() => handleQuantityChange(item.product._id, item.quantity + 1)}
                      className="w-8 h-8 bg-dark-700 hover:bg-dark-600 rounded-full flex items-center justify-center text-white transition-colors"
                    >
                      <FiPlus className="w-4 h-4" />
                    </button>
                  </div>

                  {/* Item Total */}
                  <div className="text-right">
                    <div className="text-lg font-semibold text-white">
                      {formatCurrency(item.product.price * item.quantity)}
                    </div>
                    <button
                      onClick={() => removeFromCart(item.product._id)}
                      className="text-red-400 hover:text-red-300 mt-2 transition-colors"
                    >
                      <FiTrash2 className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              </div>
            ))}

            {/* Clear Cart */}
            <div className="flex justify-end">
              <button
                onClick={clearCart}
                className="text-red-400 hover:text-red-300 text-sm transition-colors"
              >
                Clear Cart
              </button>
            </div>
          </div>

          {/* Order Summary */}
          <div className="space-y-6">
            {/* Summary Card */}
            <div className="card">
              <h3 className="text-xl font-semibold text-white mb-4">Order Summary</h3>
              
              <div className="space-y-3">
                <div className="flex justify-between">
                  <span className="text-dark-300">Subtotal</span>
                  <span className="text-white">{formatCurrency(total)}</span>
                </div>
                
                <div className="flex justify-between">
                  <span className="text-dark-300">Shipping</span>
                  <span className="text-white">
                    {calculateShipping() === 0 ? (
                      <span className="text-green-400">Free</span>
                    ) : (
                      formatCurrency(calculateShipping())
                    )}
                  </span>
                </div>
                
                <div className="flex justify-between">
                  <span className="text-dark-300">Tax (GST 18%)</span>
                  <span className="text-white">{formatCurrency(calculateTax())}</span>
                </div>
                
                <div className="border-t border-dark-600 pt-3">
                  <div className="flex justify-between">
                    <span className="text-lg font-semibold text-white">Total</span>
                    <span className="text-lg font-semibold text-primary-400">
                      {formatCurrency(calculateFinalTotal())}
                    </span>
                  </div>
                </div>
              </div>

              {total < 999 && (
                <div className="mt-4 p-3 bg-blue-500/10 border border-blue-500/20 rounded-lg">
                  <p className="text-blue-400 text-sm">
                    Add {formatCurrency(999 - total)} more for free shipping!
                  </p>
                </div>
              )}
            </div>

            {/* Checkout Button */}
            {!showCheckout ? (
              <button
                onClick={() => setShowCheckout(true)}
                className="w-full btn-primary"
              >
                Proceed to Checkout
              </button>
            ) : (
              /* Checkout Form */
              <div className="card">
                <h3 className="text-xl font-semibold text-white mb-4">Shipping Address</h3>
                
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <input
                      type="text"
                      name="name"
                      placeholder="Full Name *"
                      value={shippingAddress.name}
                      onChange={handleAddressChange}
                      className="input-field"
                      required
                    />
                    <input
                      type="tel"
                      name="phone"
                      placeholder="Phone Number *"
                      value={shippingAddress.phone}
                      onChange={handleAddressChange}
                      className="input-field"
                      required
                    />
                  </div>
                  
                  <input
                    type="email"
                    name="email"
                    placeholder="Email Address"
                    value={shippingAddress.email}
                    onChange={handleAddressChange}
                    className="input-field"
                  />
                  
                  <input
                    type="text"
                    name="street"
                    placeholder="Street Address *"
                    value={shippingAddress.street}
                    onChange={handleAddressChange}
                    className="input-field"
                    required
                  />
                  
                  <div className="grid grid-cols-2 gap-4">
                    <input
                      type="text"
                      name="city"
                      placeholder="City *"
                      value={shippingAddress.city}
                      onChange={handleAddressChange}
                      className="input-field"
                      required
                    />
                    <input
                      type="text"
                      name="state"
                      placeholder="State *"
                      value={shippingAddress.state}
                      onChange={handleAddressChange}
                      className="input-field"
                      required
                    />
                  </div>
                  
                  <input
                    type="text"
                    name="pincode"
                    placeholder="Pincode *"
                    value={shippingAddress.pincode}
                    onChange={handleAddressChange}
                    className="input-field"
                    required
                  />
                </div>

                <div className="mt-6 space-y-3">
                  <button
                    onClick={handleCheckout}
                    disabled={loading}
                    className="w-full btn-primary flex items-center justify-center"
                  >
                    {loading ? (
                      <div className="loading-spinner"></div>
                    ) : (
                      'Place Order (COD)'
                    )}
                  </button>
                  
                  <button
                    onClick={() => setShowCheckout(false)}
                    className="w-full btn-secondary"
                  >
                    Back to Cart
                  </button>
                </div>

                <div className="mt-4 text-center">
                  <p className="text-dark-300 text-sm">
                    By placing your order, you agree to our{' '}
                    <Link to="/terms" className="text-primary-400 hover:text-primary-300">
                      Terms & Conditions
                    </Link>
                  </p>
                </div>
              </div>
            )}

            {/* Payment Methods */}
            <div className="card">
              <h3 className="text-lg font-semibold text-white mb-3">Payment Methods</h3>
              <div className="space-y-2">
                <div className="flex items-center space-x-2">
                  <input
                    type="radio"
                    id="cod"
                    name="payment"
                    defaultChecked
                    className="text-primary-500"
                  />
                  <label htmlFor="cod" className="text-dark-300 text-sm">
                    Cash on Delivery (COD)
                  </label>
                </div>
                <div className="flex items-center space-x-2 opacity-50">
                  <input
                    type="radio"
                    id="online"
                    name="payment"
                    disabled
                    className="text-primary-500"
                  />
                  <label htmlFor="online" className="text-dark-300 text-sm">
                    Online Payment (Coming Soon)
                  </label>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
