import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { secureStorage } from '../utils/security';
import { 
  FiPhone, 
  FiMessageCircle, 
  FiCheck, 
  FiCheckCircle, 
  FiClock, 
  FiUser, 
  FiSmartphone,
  FiMapPin,
  FiRefreshCw,
  FiEye,
  FiX
} from 'react-icons/fi';
import { FaWhatsapp } from 'react-icons/fa';
import toast from 'react-hot-toast';
import axios from 'axios';

const AdminDashboard = () => {
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalBookings: 0,
    pendingBookings: 0,
    completedBookings: 0,
    todayBookings: 0
  });
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [showModal, setShowModal] = useState(false);

  // Check if user is admin
  useEffect(() => {
    if (!isAuthenticated || (user && user.role !== 'admin')) {
      toast.error('Access denied. Admin login required.');
      navigate('/login');
      return;
    }
    fetchBookings();
  }, [isAuthenticated, user, navigate]);

  const fetchBookings = async () => {
    try {
      setLoading(true);
      
      // Get token from multiple sources
      const tokenFromUser = user?.token;
      const tokenFromLocalStorage = localStorage.getItem('token');
      const tokenFromSecureStorage = secureStorage.get('auth_token');
      
      console.log('=== TOKEN DEBUG ===');
      console.log('Token from user:', tokenFromUser ? tokenFromUser.substring(0, 20) + '...' : 'null');
      console.log('Token from localStorage:', tokenFromLocalStorage ? tokenFromLocalStorage.substring(0, 20) + '...' : 'null');
      console.log('Token from secureStorage:', tokenFromSecureStorage ? tokenFromSecureStorage.substring(0, 20) + '...' : 'null');
      
      const token = tokenFromUser || tokenFromLocalStorage || tokenFromSecureStorage;
      
      if (!token) {
        console.error('No token found anywhere!');
        toast.error('No authentication token found');
        navigate('/login');
        return;
      }

      console.log('Using token:', token.substring(0, 20) + '...');
      console.log('Making request to: /api/admin/bookings');
      console.log('Request headers:', {
        'x-auth-token': token.substring(0, 20) + '...',
        'Content-Type': 'application/json'
      });
      
      // Fetch bookings from API using proxy with correct headers
      const response = await axios.get('/api/admin/bookings', {
        headers: {
          'x-auth-token': token,
          'Content-Type': 'application/json'
        }
      });

      console.log('API Response status:', response.status);
      console.log('API Response data:', response.data);

      if (response.data.success) {
        setBookings(response.data.bookings);
        
        // Calculate stats
        const totalBookings = response.data.bookings.length;
        const pendingBookings = response.data.bookings.filter(b => b.status === 'pending').length;
        const completedBookings = response.data.bookings.filter(b => b.status === 'completed').length;
        const todayBookings = response.data.bookings.filter(b => {
          const today = new Date().toDateString();
          const bookingDate = new Date(b.createdAt).toDateString();
          return today === bookingDate;
        }).length;

        setStats({
          totalBookings,
          pendingBookings,
          completedBookings,
          todayBookings
        });
        
        console.log('âœ… Dashboard data loaded successfully');
        toast.success('Dashboard data loaded successfully');
        return;
      } else {
        console.error('API response success=false:', response.data);
        toast.error('API returned success=false');
      }

    } catch (error) {
      console.error('=== ERROR DETAILS ===');
      console.error('Error message:', error.message);
      console.error('Error stack:', error.stack);
      
      if (error.response) {
        console.error('Response status:', error.response.status);
        console.error('Response statusText:', error.response.statusText);
        console.error('Response headers:', error.response.headers);
        console.error('Response data:', error.response.data);
        
        if (error.response.status === 401) {
          toast.error('Authentication failed. Please login again.');
          localStorage.removeItem('token');
          secureStorage.remove('auth_token');
          navigate('/login');
          return;
        } else if (error.response.status === 403) {
          toast.error('Access denied. Admin privileges required.');
          navigate('/');
          return;
        } else {
          toast.error(`API Error: ${error.response.status} - ${error.response.data?.message || 'Unknown error'}`);
        }
      } else if (error.request) {
        console.error('Request made but no response:', error.request);
        toast.error('Network error - no response from server');
      } else {
        console.error('Error setting up request:', error.message);
        toast.error('Request setup error');
      }
      
      // Fallback to demo data if API fails or for testing
      const demoBookings = [
        {
          _id: '1',
          customerDetails: {
            name: 'Rahul Kumar',
            phone: '9876543210',
            email: 'rahul@example.com',
            address: 'Ananda Gopal, Bhiringi Girls school, Benachity, Durgapur, West Bengal 713213'
          },
          deviceBrand: 'Apple (iPhone)',
          deviceModel: 'iPhone 14 Pro',
          serviceType: 'Display/Screen Issues',
          issueDescription: 'Screen is cracked and touch is not working properly',
          status: 'pending',
          preferredDate: '2024-01-15',
          preferredTime: '2:00 PM - 4:00 PM',
          createdAt: new Date().toISOString(),
          isGuestBooking: false
        },
        {
          _id: '2',
          customerDetails: {
            name: 'Priya Sharma',
            phone: '9123456789',
            email: 'priya@example.com',
            address: 'Sector 15, Noida, UP 201301'
          },
          deviceBrand: 'Samsung',
          deviceModel: 'Galaxy S23 Ultra',
          serviceType: 'Battery Problems',
          issueDescription: 'Battery drains very fast, need replacement',
          status: 'accepted',
          preferredDate: '2024-01-16',
          preferredTime: '10:00 AM - 12:00 PM',
          createdAt: new Date(Date.now() - 86400000).toISOString(),
          isGuestBooking: true
        },
        {
          _id: '3',
          customerDetails: {
            name: 'Amit Singh',
            phone: '9800999600',
            email: 'amit@example.com',
            address: 'Gurgaon, Haryana 122001'
          },
          deviceBrand: 'OnePlus',
          deviceModel: 'OnePlus 11',
          serviceType: 'Charging Port Issues',
          issueDescription: 'Phone not charging properly, port seems loose',
          status: 'completed',
          preferredDate: '2024-01-14',
          preferredTime: '4:00 PM - 6:00 PM',
          createdAt: new Date(Date.now() - 172800000).toISOString(),
          isGuestBooking: false
        },
        {
          _id: '4',
          customerDetails: {
            name: 'Sneha Patel',
            phone: '9988776655',
            email: 'sneha@example.com',
            address: 'Ananda Gopal, Bhiringi Girls school, Benachity, Durgapur, West Bengal 713213'
          },
          deviceBrand: 'Xiaomi',
          deviceModel: 'Mi 13 Pro',
          serviceType: 'Software Issues',
          issueDescription: 'Phone keeps restarting randomly. Apps crash frequently.',
          status: 'in-progress',
          preferredDate: '2024-01-17',
          preferredTime: '11:00 AM - 1:00 PM',
          createdAt: new Date(Date.now() - 43200000).toISOString(),
          isGuestBooking: true
        },
        {
          _id: '5',
          customerDetails: {
            name: 'Ravi Gupta',
            phone: '9876543211',
            email: 'ravi@example.com',
            address: 'Park Street, Kolkata, West Bengal 700016'
          },
          deviceBrand: 'Vivo',
          deviceModel: 'V29 Pro',
          serviceType: 'Camera Issues',
          issueDescription: 'Rear camera not working, shows black screen',
          status: 'pending',
          preferredDate: '2024-01-18',
          preferredTime: '3:00 PM - 5:00 PM',
          createdAt: new Date(Date.now() - 21600000).toISOString(),
          isGuestBooking: false
        }
      ];

      console.log('Using demo data as fallback');
      setBookings(demoBookings);
      
      // Calculate stats for demo data
      const totalBookings = demoBookings.length;
      const pendingBookings = demoBookings.filter(b => b.status === 'pending').length;
      const completedBookings = demoBookings.filter(b => b.status === 'completed').length;
      const todayBookings = demoBookings.filter(b => {
        const today = new Date().toDateString();
        const bookingDate = new Date(b.createdAt).toDateString();
        return today === bookingDate;
      }).length;

      setStats({
        totalBookings,
        pendingBookings,
        completedBookings,
        todayBookings
      });
      
      toast.success('Dashboard loaded with demo data');
    } finally {
      setLoading(false);
    }
  };

  const handleCall = (phone) => {
    window.open(`tel:${phone}`, '_self');
  };

  const handleWhatsApp = (phone, customerName, deviceBrand, issue) => {
    const message = `Hello ${customerName}, this is Mobi repair. We received your repair request for ${deviceBrand} - ${issue}. We will contact you soon for pickup.`;
    const whatsappUrl = `https://wa.me/91${phone.replace(/\D/g, '')}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  const updateBookingStatus = async (bookingId, newStatus) => {
    try {
      // Get token from AuthContext or localStorage
      const token = user?.token || localStorage.getItem('token') || secureStorage.get('auth_token');
      if (!token) {
        toast.error('No authentication token found');
        return;
      }

      // Try to update via API using proxy
      const response = await axios.put(`/api/admin/bookings/${bookingId}`, 
        { status: newStatus },
        {
          headers: {
            'x-auth-token': token,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        toast.success(`Booking ${newStatus} successfully!`);
        fetchBookings(); // Refresh data
        return;
      }
    } catch (error) {
      console.error('API update failed:', error);
      
      if (error.response) {
        console.error('Response status:', error.response.status);
        console.error('Response data:', error.response.data);
        
        if (error.response.status === 401) {
          toast.error('Authentication failed. Please login again.');
          localStorage.removeItem('token');
          secureStorage.remove('auth_token');
          navigate('/login');
          return;
        }
      }
      
      toast.error('Failed to update booking status');
    }

    // Fallback to demo implementation
    setBookings(prev => prev.map(booking => 
      booking._id === bookingId 
        ? { ...booking, status: newStatus }
        : booking
    ));
    
    toast.success(`Booking ${newStatus} successfully! (Demo mode)`);
    
    // Update stats
    setTimeout(() => fetchBookings(), 500);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30';
      case 'accepted': return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
      case 'completed': return 'bg-green-500/20 text-green-400 border-green-500/30';
      default: return 'bg-gray-500/20 text-gray-400 border-gray-500/30';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending': return <FiClock className="w-4 h-4" />;
      case 'accepted': return <FiCheck className="w-4 h-4" />;
      case 'completed': return <FiCheckCircle className="w-4 h-4" />;
      default: return <FiClock className="w-4 h-4" />;
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-pink-500 mx-auto mb-4"></div>
          <p className="text-gray-300">Loading admin dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 text-white mobile-safe-area">
      {/* Header */}
      <div className="bg-gradient-to-r from-pink-500/10 to-purple-500/10 border-b border-pink-500/20 py-4 sm:py-6 lg:py-8">
        <div className="mobile-container">
          <div className="mobile-flex-col items-start sm:items-center justify-between gap-4">
            <div>
              <h1 className="mobile-heading font-bold text-white mb-2">Admin Dashboard</h1>
              <p className="text-gray-300 mobile-text">Manage repair bookings and customer requests</p>
            </div>
            <button
              onClick={fetchBookings}
              className="mobile-btn bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700 text-white rounded-lg transition-all duration-300"
            >
              <FiRefreshCw className="w-4 h-4 mr-2" />
              <span>Refresh</span>
            </button>
          </div>
        </div>
      </div>

      <div className="mobile-container py-4 sm:py-6 lg:py-8">{/* Rest of content */}
        {/* Stats Cards */}
        <div className="mobile-stats-grid mb-6 sm:mb-8">
          <div className="mobile-stats-card bg-gradient-to-br from-blue-500/10 to-blue-600/10 border-blue-500/20">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-blue-400 text-xs sm:text-sm font-medium">Total</p>
                <p className="text-lg sm:text-xl lg:text-2xl font-bold text-white">{stats.totalBookings}</p>
              </div>
              <div className="text-blue-400 text-lg sm:text-xl lg:text-2xl">ðŸ“±</div>
            </div>
          </div>

          <div className="mobile-stats-card bg-gradient-to-br from-yellow-500/10 to-yellow-600/10 border-yellow-500/20">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-yellow-400 text-xs sm:text-sm font-medium">Pending</p>
                <p className="text-lg sm:text-xl lg:text-2xl font-bold text-white">{stats.pendingBookings}</p>
              </div>
              <div className="text-yellow-400 text-lg sm:text-xl lg:text-2xl">â³</div>
            </div>
          </div>

          <div className="mobile-stats-card bg-gradient-to-br from-green-500/10 to-green-600/10 border-green-500/20">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-green-400 text-xs sm:text-sm font-medium">Done</p>
                <p className="text-lg sm:text-xl lg:text-2xl font-bold text-white">{stats.completedBookings}</p>
              </div>
              <div className="text-green-400 text-lg sm:text-xl lg:text-2xl">âœ…</div>
            </div>
          </div>

          <div className="mobile-stats-card bg-gradient-to-br from-purple-500/10 to-purple-600/10 border-purple-500/20">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-purple-400 text-xs sm:text-sm font-medium">Today</p>
                <p className="text-lg sm:text-xl lg:text-2xl font-bold text-white">{stats.todayBookings}</p>
              </div>
              <div className="text-purple-400 text-lg sm:text-xl lg:text-2xl">ðŸ“…</div>
            </div>
          </div>
        </div>

        {/* Bookings Table - Desktop */}
        <div className="desktop-only mobile-card">
          <div className="p-4 sm:p-6 border-b border-gray-700/50">
            <h2 className="text-lg sm:text-xl font-bold text-white">Repair Bookings</h2>
            <p className="text-gray-400 text-sm mt-1">Manage all customer repair requests</p>
          </div>

          <div className="mobile-table-wrapper">
            <table className="mobile-table">
              <thead className="bg-gray-800/50">
                <tr>
                  <th className="text-left p-3 sm:p-4 text-gray-300 font-medium text-sm">Customer</th>
                  <th className="text-left p-3 sm:p-4 text-gray-300 font-medium text-sm hidden sm:table-cell">Device</th>
                  <th className="text-left p-3 sm:p-4 text-gray-300 font-medium text-sm">Issue</th>
                  <th className="text-left p-3 sm:p-4 text-gray-300 font-medium text-sm hidden md:table-cell">Status</th>
                  <th className="text-left p-3 sm:p-4 text-gray-300 font-medium text-sm hidden lg:table-cell">Date</th>
                  <th className="text-left p-3 sm:p-4 text-gray-300 font-medium text-sm">Actions</th>
                </tr>
              </thead>
              <tbody>
                {bookings.map((booking) => (
                  <tr key={booking._id} className="border-t border-gray-700/30 hover:bg-gray-800/30 transition-colors">
                    <td className="p-3 sm:p-4">
                      <div className="flex items-center space-x-2 sm:space-x-3">
                        <div className="w-8 h-8 sm:w-10 sm:h-10 bg-gradient-to-r from-pink-500 to-purple-600 rounded-full flex items-center justify-center flex-shrink-0">
                          <FiUser className="w-4 h-4 sm:w-5 sm:h-5 text-white" />
                        </div>
                        <div className="min-w-0">
                          <p className="text-white font-medium text-sm sm:text-base mobile-truncate">{booking.customerDetails.name}</p>
                          <p className="text-gray-400 text-xs sm:text-sm mobile-truncate">{booking.customerDetails.phone}</p>
                        </div>
                      </div>
                    </td>
                    <td className="p-3 sm:p-4 hidden sm:table-cell">
                      <div className="flex items-center space-x-2">
                        <FiSmartphone className="w-4 h-4 text-blue-400 flex-shrink-0" />
                        <div className="min-w-0">
                          <p className="text-white font-medium text-sm mobile-truncate">{booking.deviceBrand}</p>
                          <p className="text-gray-400 text-xs mobile-truncate">{booking.deviceModel || 'Model not specified'}</p>
                        </div>
                      </div>
                    </td>
                    <td className="p-3 sm:p-4">
                      <div className="min-w-0">
                        <p className="text-white font-medium text-sm mobile-truncate">{booking.serviceType}</p>
                        <p className="text-gray-400 text-xs mobile-truncate-sm">{booking.issueDescription}</p>
                      </div>
                    </td>
                    <td className="p-3 sm:p-4 hidden md:table-cell">
                      <span className={`inline-flex items-center space-x-1 px-2 sm:px-3 py-1 rounded-full text-xs font-medium border ${getStatusColor(booking.status)}`}>
                        {getStatusIcon(booking.status)}
                        <span className="capitalize hidden sm:inline">{booking.status}</span>
                      </span>
                    </td>
                    <td className="p-3 sm:p-4 hidden lg:table-cell">
                      <p className="text-gray-300 text-xs sm:text-sm">{formatDate(booking.createdAt)}</p>
                    </td>
                    <td className="p-3 sm:p-4">
                      <div className="mobile-flex-wrap">
                        <button
                          onClick={() => handleCall(booking.customerDetails.phone)}
                          className="mobile-action-btn bg-green-500/20 hover:bg-green-500/30 text-green-400"
                          title="Call Customer"
                        >
                          <FiPhone className="w-3 h-3 sm:w-4 sm:h-4" />
                        </button>
                        <button
                          onClick={() => handleWhatsApp(
                            booking.customerDetails.phone,
                            booking.customerDetails.name,
                            booking.deviceBrand,
                            booking.serviceType
                          )}
                          className="mobile-action-btn bg-green-600/20 hover:bg-green-600/30 text-green-400"
                          title="WhatsApp Customer"
                        >
                          <FaWhatsapp className="w-3 h-3 sm:w-4 sm:h-4" />
                        </button>
                        <button
                          onClick={() => {
                            setSelectedBooking(booking);
                            setShowModal(true);
                          }}
                          className="mobile-action-btn bg-blue-500/20 hover:bg-blue-500/30 text-blue-400"
                          title="View Details"
                        >
                          <FiEye className="w-3 h-3 sm:w-4 sm:h-4" />
                        </button>
                        {booking.status === 'pending' && (
                          <button
                            onClick={() => updateBookingStatus(booking._id, 'accepted')}
                            className="mobile-action-btn bg-blue-500/20 hover:bg-blue-500/30 text-blue-400"
                            title="Accept Booking"
                          >
                            <FiCheck className="w-3 h-3 sm:w-4 sm:h-4" />
                          </button>
                        )}
                        {booking.status === 'accepted' && (
                          <button
                            onClick={() => updateBookingStatus(booking._id, 'completed')}
                            className="mobile-action-btn bg-green-500/20 hover:bg-green-500/30 text-green-400"
                            title="Mark Completed"
                          >
                            <FiCheckCircle className="w-3 h-3 sm:w-4 sm:h-4" />
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* Bookings Cards - Mobile */}
        <div className="mobile-only mobile-space-y-sm">
          <div className="mobile-card">
            <h2 className="text-lg font-bold text-white mb-2">Repair Bookings</h2>
            <p className="text-gray-400 text-sm mb-4">Manage all customer repair requests</p>
          </div>
          
          {bookings.map((booking) => (
            <div key={booking._id} className="mobile-table-card">
              <div className="mobile-table-card-header">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-gradient-to-r from-pink-500 to-purple-600 rounded-full flex items-center justify-center">
                    <FiUser className="w-5 h-5 text-white" />
                  </div>
                  <div>
                    <p className="text-white font-medium">{booking.customerDetails.name}</p>
                    <p className="text-gray-400 text-sm">{booking.customerDetails.phone}</p>
                  </div>
                </div>
                <span className={`inline-flex items-center space-x-1 px-3 py-1 rounded-full text-xs font-medium border ${getStatusColor(booking.status)}`}>
                  {getStatusIcon(booking.status)}
                  <span className="capitalize">{booking.status}</span>
                </span>
              </div>
              
              <div className="mobile-table-card-content">
                <div className="mobile-table-card-row">
                  <span className="mobile-table-card-label">Device:</span>
                  <span className="mobile-table-card-value">{booking.deviceBrand} {booking.deviceModel}</span>
                </div>
                <div className="mobile-table-card-row">
                  <span className="mobile-table-card-label">Issue:</span>
                  <span className="mobile-table-card-value">{booking.serviceType}</span>
                </div>
                <div className="mobile-table-card-row">
                  <span className="mobile-table-card-label">Description:</span>
                  <span className="mobile-table-card-value">{booking.issueDescription}</span>
                </div>
                <div className="mobile-table-card-row">
                  <span className="mobile-table-card-label">Date:</span>
                  <span className="mobile-table-card-value">{formatDate(booking.createdAt)}</span>
                </div>
              </div>
              
              <div className="mobile-actions mt-4 pt-3 border-t border-gray-700/30">
                <button
                  onClick={() => handleCall(booking.customerDetails.phone)}
                  className="mobile-btn-secondary bg-green-500/20 hover:bg-green-500/30 text-green-400 border-green-500/30"
                >
                  <FiPhone className="w-4 h-4 mr-2" />
                  Call
                </button>
                <button
                  onClick={() => handleWhatsApp(
                    booking.customerDetails.phone,
                    booking.customerDetails.name,
                    booking.deviceBrand,
                    booking.serviceType
                  )}
                  className="mobile-btn-secondary bg-green-600/20 hover:bg-green-600/30 text-green-400 border-green-600/30"
                >
                  <FaWhatsapp className="w-4 h-4 mr-2" />
                  WhatsApp
                </button>
                <button
                  onClick={() => {
                    setSelectedBooking(booking);
                    setShowModal(true);
                  }}
                  className="mobile-btn-secondary bg-blue-500/20 hover:bg-blue-500/30 text-blue-400 border-blue-500/30"
                >
                  <FiEye className="w-4 h-4 mr-2" />
                  Details
                </button>
                {booking.status === 'pending' && (
                  <button
                    onClick={() => updateBookingStatus(booking._id, 'accepted')}
                    className="mobile-btn-primary"
                  >
                    <FiCheck className="w-4 h-4 mr-2" />
                    Accept
                  </button>
                )}
                {booking.status === 'accepted' && (
                  <button
                    onClick={() => updateBookingStatus(booking._id, 'completed')}
                    className="mobile-btn-primary bg-gradient-to-r from-green-500 to-green-600"
                  >
                    <FiCheckCircle className="w-4 h-4 mr-2" />
                    Complete
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Booking Details Modal */}
      {showModal && selectedBooking && (
        <div className="mobile-modal bg-black/50 backdrop-blur-sm">
          <div className="mobile-modal-content">
            <div className="mobile-header">
              <div className="mobile-header-content">
                <h3 className="mobile-header-title">Booking Details</h3>
                <button
                  onClick={() => setShowModal(false)}
                  className="mobile-action-btn hover:bg-gray-700"
                >
                  <FiX className="w-5 h-5 text-gray-400" />
                </button>
              </div>
            </div>
            
            <div className="p-4 sm:p-6 mobile-space-y">
              {/* Customer Info */}
              <div className="mobile-card">
                <h4 className="text-lg font-semibold text-white mb-3">Customer Information</h4>
                <div className="mobile-grid-2 gap-4">
                  <div>
                    <p className="text-gray-400 text-sm">Name</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.name}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Phone</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.phone}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Email</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.email}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Address</p>
                    <p className="text-white font-medium">{selectedBooking.customerDetails.address}</p>
                  </div>
                </div>
              </div>

              {/* Device Info */}
              <div className="mobile-card">
                <h4 className="text-lg font-semibold text-white mb-3">Device Information</h4>
                <div className="mobile-grid-2 gap-4">
                  <div>
                    <p className="text-gray-400 text-sm">Brand</p>
                    <p className="text-white font-medium">{selectedBooking.deviceBrand}</p>
                  </div>
                  <div>
                    <p className="text-gray-400 text-sm">Model</p>
                    <p className="text-white font-medium">{selectedBooking.deviceModel || 'Not specified'}</p>
                  </div>
                </div>
              </div>

              {/* Issue Details */}
              <div className="mobile-card">
                <h4 className="text-lg font-semibold text-white mb-3">Issue Details</h4>
                <div>
                  <p className="text-gray-400 text-sm">Issue Type</p>
                  <p className="text-white font-medium mb-3">{selectedBooking.serviceType}</p>
                  <p className="text-gray-400 text-sm">Description</p>
                  <p className="text-white">{selectedBooking.issueDescription}</p>
                </div>
              </div>

              {/* Schedule */}
              {selectedBooking.preferredDate && (
                <div className="mobile-card">
                  <h4 className="text-lg font-semibold text-white mb-3">Preferred Schedule</h4>
                  <div className="mobile-grid-2 gap-4">
                    <div>
                      <p className="text-gray-400 text-sm">Date</p>
                      <p className="text-white font-medium">{selectedBooking.preferredDate}</p>
                    </div>
                    <div>
                      <p className="text-gray-400 text-sm">Time</p>
                      <p className="text-white font-medium">{selectedBooking.preferredTime || 'Any time'}</p>
                    </div>
                  </div>
                </div>
              )}

              {/* Action Buttons */}
              <div className="mobile-actions pt-4 border-t border-gray-700">
                <button
                  onClick={() => handleCall(selectedBooking.customerDetails.phone)}
                  className="mobile-btn-secondary bg-green-500/20 hover:bg-green-500/30 text-green-400 border-green-500/30"
                >
                  <FiPhone className="w-4 h-4 mr-2" />
                  Call Customer
                </button>
                <button
                  onClick={() => handleWhatsApp(
                    selectedBooking.customerDetails.phone,
                    selectedBooking.customerDetails.name,
                    selectedBooking.deviceBrand,
                    selectedBooking.serviceType
                  )}
                  className="mobile-btn-secondary bg-green-600/20 hover:bg-green-600/30 text-green-400 border-green-600/30"
                >
                  <FaWhatsapp className="w-4 h-4 mr-2" />
                  WhatsApp
                </button>
                {selectedBooking.status === 'pending' && (
                  <button
                    onClick={() => {
                      updateBookingStatus(selectedBooking._id, 'accepted');
                      setShowModal(false);
                    }}
                    className="mobile-btn-primary"
                  >
                    <FiCheck className="w-4 h-4 mr-2" />
                    Accept
                  </button>
                )}
                {selectedBooking.status === 'accepted' && (
                  <button
                    onClick={() => {
                      updateBookingStatus(selectedBooking._id, 'completed');
                      setShowModal(false);
                    }}
                    className="mobile-btn-primary bg-gradient-to-r from-green-500 to-green-600"
                  >
                    <FiCheckCircle className="w-4 h-4 mr-2" />
                    Mark Completed
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
