import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { 
  FiUser, 
  FiTool, 
  FiShoppingBag, 
  FiClock, 
  FiCheck, 
  FiEye,
  FiPlus
} from 'react-icons/fi';
import axios from 'axios';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [bookings, setBookings] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      // Demo mode - use mock data
      if (localStorage.getItem('token') === 'demo-jwt-token-123') {
        const mockBookings = [
          {
            _id: '1',
            deviceBrand: 'Apple',
            deviceModel: 'iPhone 15 Pro',
            serviceType: 'Display Replacement',
            status: 'In Progress',
            totalCost: 12999,
            createdAt: new Date().toISOString()
          },
          {
            _id: '2',
            deviceBrand: 'Samsung',
            deviceModel: 'Galaxy S24',
            serviceType: 'Battery Replacement',
            status: 'Completed',
            totalCost: 2999,
            createdAt: new Date(Date.now() - 86400000).toISOString()
          }
        ];
        
        const mockOrders = [
          {
            _id: '1',
            orderNumber: 'GF202412300001',
            items: [{ product: { name: 'iPhone Case' }, quantity: 1 }],
            orderStatus: 'Delivered',
            total: 1999,
            createdAt: new Date(Date.now() - 172800000).toISOString()
          }
        ];
        
        setBookings(mockBookings);
        setOrders(mockOrders);
        setLoading(false);
        return;
      }
      
      const [bookingsRes, ordersRes] = await Promise.all([
        axios.get('/api/repairs/my-bookings'),
        axios.get('/api/orders/my-orders')
      ]);
      
      setBookings(bookingsRes.data.bookings || []);
      setOrders(ordersRes.data.orders || []);
    } catch (error) {
      console.error('Error fetching user data:', error);
      // Don't show error toast in demo mode
      if (localStorage.getItem('token') !== 'demo-jwt-token-123') {
        toast.error('Failed to load dashboard data');
      }
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'pending':
        return 'text-yellow-400 bg-yellow-400/10';
      case 'confirmed':
        return 'text-blue-400 bg-blue-400/10';
      case 'in progress':
      case 'processing':
        return 'text-purple-400 bg-purple-400/10';
      case 'completed':
      case 'delivered':
        return 'text-green-400 bg-green-400/10';
      case 'cancelled':
        return 'text-red-400 bg-red-400/10';
      default:
        return 'text-gray-400 bg-gray-400/10';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  const stats = [
    {
      title: 'Total Bookings',
      value: bookings.length,
      icon: <FiTool className="w-6 h-6" />,
      color: 'text-blue-400'
    },
    {
      title: 'Total Orders',
      value: orders.length,
      icon: <FiShoppingBag className="w-6 h-6" />,
      color: 'text-green-400'
    },
    {
      title: 'Pending Repairs',
      value: bookings.filter(b => ['pending', 'confirmed', 'in progress'].includes(b.status?.toLowerCase())).length,
      icon: <FiClock className="w-6 h-6" />,
      color: 'text-yellow-400'
    },
    {
      title: 'Completed Repairs',
      value: bookings.filter(b => b.status?.toLowerCase() === 'completed').length,
      icon: <FiCheck className="w-6 h-6" />,
      color: 'text-purple-400'
    }
  ];

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen section-padding relative overflow-hidden bg-black">
      {/* Animated Background */}
      <div className="gradient-mesh"></div>
      <div className="glow-orb glow-orb-1"></div>
      <div className="glow-orb glow-orb-2"></div>
      <div className="glow-orb glow-orb-3"></div>
      
      {/* Floating Elements */}
      <div className="floating-elements">
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
        <div className="floating-element"></div>
      </div>
      
      {/* Particles */}
      <div className="particles">
        {[...Array(25)].map((_, i) => (
          <div
            key={i}
            className="particle"
            style={{
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 15}s`,
              animationDuration: `${15 + Math.random() * 10}s`
            }}
          ></div>
        ))}
      </div>
      <div className="container-max relative z-10">
        {/* Header */}
        <div className="mb-8 text-reveal">
          <h1 className="text-3xl font-bold text-white mb-2">
            Welcome back, {user?.name}!
          </h1>
          <p className="text-dark-300 text-reveal-delay-1">
            Manage your repair bookings and orders from your dashboard
          </p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-6 mb-8 stagger-animation">
          {stats.map((stat, index) => (
            <div key={index} className="card glow-on-hover float-card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-dark-300 text-sm mb-1">{stat.title}</p>
                  <p className="text-2xl font-bold text-white">{stat.value}</p>
                </div>
                <div className={stat.color}>
                  {stat.icon}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Navigation Tabs */}
        <div className="flex space-x-1 mb-8 bg-dark-800 p-1 rounded-lg card-entrance">
          {[
            { id: 'overview', label: 'Overview', icon: <FiUser className="w-4 h-4" /> },
            { id: 'bookings', label: 'Repair Bookings', icon: <FiTool className="w-4 h-4" /> },
            { id: 'orders', label: 'Orders', icon: <FiShoppingBag className="w-4 h-4" /> }
          ].map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center space-x-2 px-4 py-2 rounded-md transition-colors morph-button ${
                activeTab === tab.id
                  ? 'bg-primary-500 text-white'
                  : 'text-dark-300 hover:text-white hover:bg-dark-700'
              }`}
            >
              {tab.icon}
              <span>{tab.label}</span>
            </button>
          ))}
        </div>

        {/* Tab Content */}
        {activeTab === 'overview' && (
          <div className="space-y-8">
            {/* Quick Actions */}
            <div className="card glow-on-hover card-entrance">
              <h2 className="text-xl font-semibold text-white mb-4">Quick Actions</h2>
              <div className="grid md:grid-cols-2 gap-4">
                <Link
                  to="/repair-booking"
                  className="flex items-center space-x-3 p-4 bg-dark-700 rounded-lg hover:bg-dark-600 transition-colors morph-button"
                >
                  <div className="w-10 h-10 bg-primary-500 rounded-lg flex items-center justify-center enhanced-glow">
                    <FiPlus className="w-5 h-5 text-white" />
                  </div>
                  <div>
                    <h3 className="font-medium text-white">Book New Repair</h3>
                    <p className="text-dark-300 text-sm">Schedule a repair service</p>
                  </div>
                </Link>
                <Link
                  to="/store"
                  className="flex items-center space-x-3 p-4 bg-dark-700 rounded-lg hover:bg-dark-600 transition-colors morph-button"
                >
                  <div className="w-10 h-10 bg-green-500 rounded-lg flex items-center justify-center enhanced-glow">
                    <FiShoppingBag className="w-5 h-5 text-white" />
                  </div>
                  <div>
                    <h3 className="font-medium text-white">Shop Products</h3>
                    <p className="text-dark-300 text-sm">Browse mobile accessories</p>
                  </div>
                </Link>
              </div>
            </div>

            {/* Recent Activity */}
            <div className="grid lg:grid-cols-2 gap-8">
              {/* Recent Bookings */}
              <div className="card glow-on-hover card-entrance">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-semibold text-white">Recent Bookings</h2>
                  <Link
                    to="#"
                    onClick={() => setActiveTab('bookings')}
                    className="text-primary-400 hover:text-primary-300 text-sm"
                  >
                    View All
                  </Link>
                </div>
                <div className="space-y-3">
                  {bookings.slice(0, 3).map((booking) => (
                    <div key={booking._id} className="flex items-center justify-between p-3 bg-dark-700 rounded-lg glow-on-hover">
                      <div>
                        <p className="text-white font-medium">{booking.deviceBrand} {booking.deviceModel}</p>
                        <p className="text-dark-300 text-sm">{booking.serviceType}</p>
                      </div>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(booking.status)}`}>
                        {booking.status}
                      </span>
                    </div>
                  ))}
                  {bookings.length === 0 && (
                    <p className="text-dark-300 text-center py-4">No bookings yet</p>
                  )}
                </div>
              </div>

              {/* Recent Orders */}
              <div className="card glow-on-hover card-entrance">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-semibold text-white">Recent Orders</h2>
                  <Link
                    to="#"
                    onClick={() => setActiveTab('orders')}
                    className="text-primary-400 hover:text-primary-300 text-sm"
                  >
                    View All
                  </Link>
                </div>
                <div className="space-y-3">
                  {orders.slice(0, 3).map((order) => (
                    <div key={order._id} className="flex items-center justify-between p-3 bg-dark-700 rounded-lg glow-on-hover">
                      <div>
                        <p className="text-white font-medium">Order #{order.orderNumber}</p>
                        <p className="text-dark-300 text-sm">{formatCurrency(order.total)}</p>
                      </div>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(order.orderStatus)}`}>
                        {order.orderStatus}
                      </span>
                    </div>
                  ))}
                  {orders.length === 0 && (
                    <p className="text-dark-300 text-center py-4">No orders yet</p>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {activeTab === 'bookings' && (
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-white">Repair Bookings</h2>
              <Link to="/repair-booking" className="btn-primary">
                Book New Repair
              </Link>
            </div>
            
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-dark-600">
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Device</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Service</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Date</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Status</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Total</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {bookings.map((booking) => (
                    <tr key={booking._id} className="border-b border-dark-700">
                      <td className="py-4 px-4">
                        <div>
                          <p className="text-white font-medium">{booking.deviceBrand}</p>
                          <p className="text-dark-300 text-sm">{booking.deviceModel}</p>
                        </div>
                      </td>
                      <td className="py-4 px-4 text-dark-300">{booking.serviceType}</td>
                      <td className="py-4 px-4 text-dark-300">{formatDate(booking.createdAt)}</td>
                      <td className="py-4 px-4">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(booking.status)}`}>
                          {booking.status}
                        </span>
                      </td>
                      <td className="py-4 px-4 text-white font-medium">
                        {formatCurrency(booking.totalCost)}
                      </td>
                      <td className="py-4 px-4">
                        <button className="text-primary-400 hover:text-primary-300 mr-2">
                          <FiEye className="w-4 h-4" />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {bookings.length === 0 && (
                <div className="text-center py-8">
                  <p className="text-dark-300 mb-4">No repair bookings found</p>
                  <Link to="/repair-booking" className="btn-primary">
                    Book Your First Repair
                  </Link>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === 'orders' && (
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-white">Orders</h2>
              <Link to="/store" className="btn-primary">
                Shop Now
              </Link>
            </div>
            
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-dark-600">
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Order #</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Date</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Items</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Status</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Total</th>
                    <th className="text-left py-3 px-4 text-dark-300 font-medium">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.map((order) => (
                    <tr key={order._id} className="border-b border-dark-700">
                      <td className="py-4 px-4 text-white font-medium">#{order.orderNumber}</td>
                      <td className="py-4 px-4 text-dark-300">{formatDate(order.createdAt)}</td>
                      <td className="py-4 px-4 text-dark-300">{order.items.length} items</td>
                      <td className="py-4 px-4">
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(order.orderStatus)}`}>
                          {order.orderStatus}
                        </span>
                      </td>
                      <td className="py-4 px-4 text-white font-medium">
                        {formatCurrency(order.total)}
                      </td>
                      <td className="py-4 px-4">
                        <button className="text-primary-400 hover:text-primary-300 mr-2">
                          <FiEye className="w-4 h-4" />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {orders.length === 0 && (
                <div className="text-center py-8">
                  <p className="text-dark-300 mb-4">No orders found</p>
                  <Link to="/store" className="btn-primary">
                    Start Shopping
                  </Link>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
